%psql
DROP TABLE IF EXISTS eu_countries;

CREATE TABLE eu_countries(
  countryname VARCHAR(24),
  countrycode VARCHAR(4),
  region VARCHAR(4)
  );

%psql

DROP TABLE IF EXISTS tax_2020;

CREATE TABLE tax_2020(
  ssn VARCHAR(12),
  fed_tax NUMERIC (8, 2),
  state_tax NUMERIC (8, 2),
  local_tax NUMERIC (8, 2)
  );
