%jdbc(hive)

DROP DATABASE IF EXISTS cloudfin CASCADE;

CREATE DATABASE cloudfin;
USE cloudfin;

%jdbc(hive)

DROP TABLE IF EXISTS eu_country_stage;

CREATE EXTERNAL TABLE eu_country_stage(
  country_name STRING,
  country_code STRING,
  region STRING
  )
ROW FORMAT DELIMITED
FIELDS TERMINATED BY ','
LOCATION '/warehouse/tablespace/external/hive/cloudfin.db/eu_country_stage';

LOAD DATA INPATH '/data/cloudfin/eu_country.csv' OVERWRITE INTO TABLE eu_country_stage;
