%jdbc(hive_interactive)
DROP DATABASE IF EXISTS movie CASCADE;
CREATE DATABASE movie;
USE movie;

%jdbc(hive_interactive)
DROP TABLE IF EXISTS movie_crew_stage;
-- Contains the information for directors and writers
CREATE EXTERNAL TABLE movie_crew_stage(
                        tconst string,
                        directors ARRAY<STRING>,
                        writers ARRAY<STRING>
                        ) 
ROW FORMAT DELIMITED 
FIELDS TERMINATED BY '\t' 
LOCATION '/warehouse/tablespace/external/hive/movie_crew_stage/';

-- Load data movie_crew
LOAD DATA  INPATH '/data/movie/title.crew.tsv' OVERWRITE INTO TABLE movie_crew_stage;

-- Remove header for movie_crew
ALTER TABLE movie_crew_stage SET TBLPROPERTIES ("skip.header.line.count"="1");

%jdbc(hive_interactive)
DROP TABLE IF EXISTS rating_stage;
-- Ratings and vote information for titles
CREATE EXTERNAL TABLE rating_stage(
                        tconst string,
                        averageRting double,
                        numVotes int
                        ) 
ROW FORMAT DELIMITED 
FIELDS TERMINATED BY '\t' 
LOCATION '/warehouse/tablespace/external/hive/rating_stage/';


-- Load data ratings
LOAD DATA  INPATH '/data/movie/title.ratings.tsv' OVERWRITE INTO TABLE rating_stage;

-- Remove header for ratings
ALTER TABLE rating_stage SET TBLPROPERTIES ("skip.header.line.count"="1");

%jdbc(hive_interactive)
