%pyspark

sc.setJobGroup("Querying Tables and Views with SQL","View the list of current Hive tables and temporary views in the devsh database")
for table in spark.catalog.listTables("devsh"): print(table)

%pyspark

sc.setJobGroup("Querying Tables and Views with SQL","List the schema (column definitions) of the devsh.accounts table")
for column in spark.catalog.listColumns("accounts","devsh"): print(column)
